if(sort==null){
var fk = sk = 1,
sort1 = [],
sort2 = [];
push=(title,url,type1,type2)=>{
		json = JSON.stringify({
				title: title,
				url: url?url:"",
				style: {
						layout_flexGrow: 1,
						layout_flexBasisPercent: type1
					}
			});
