function readEncrypt(novelid,flag) {
     	FORUMSIGNTAG = 1;
       READSIGNTAG = 0;
	      strArr = [cookie.getKey('http://m.jjwxc.net','sid'),novelid,"autosubscribe",flag];

	      sb = new Date().getTime().toString();
         length = strArr.length;
        for ( i2 = READSIGNTAG; i2 < length; i2 += FORUMSIGNTAG) {
            str = strArr[i2];
            sb+=":";
            sb+=str;
        }

	    en= java.createSymmetricCrypto("DES/CBC/PKCS5Padding", String("KK!%G3JdCHJxpAF3%Vg9pN"), String("1ae2c94b")).encryptBase64(sb)

